import { useEffect, useState } from "react";
import Shimmer from "./Shimmer";

const RestaurantMenu = () => {

  const [resInfo, setResInfo] = useState([]);

  useEffect(() => {
    fetchMenu()
  },[]);

  const fetchMenu = async () => {
    const data = await fetch("https://www.swiggy.com/dapi/menu/pl?page-type=REGULAR_MENU&complete-menu=true&lat=12.9351929&lng=77.62448069999999&restaurantId=502999");

    const json = await data.json();
    setResInfo(json.data);

    

    
  }


  const { name, cuisines, costForTwoMessage } =
    resInfo ? resInfo.cards[0].card.card.info: [];

  const { itemCards } =
    resInfo?.cards[2]?.groupedCard?.cardGroupMap?.REGULAR?.cards[1]?.card?.card;
  if(resInfo === null) return <Shimmer />;


  //const {itemCards} = resInfo.
  return (

    <div>
      <h1>{name}</h1>
      <h2>Menu</h2>
      <ul>
        <li>Biryani</li>
        <li>Manchurian</li>
        <li>Coke</li>
      </ul>
    </div>
  );
};

export default RestaurantMenu;
