import { CDN_URL } from "../utils/constants";

const ItemCard = ({ ItemData }) => {
    console.log(ItemData)
    return (
        <div>
            {ItemData.map(item => (
                    <div className="text-left p-2 m-2 border-b-2 border-gray-200"> 
                    <div className="flex justify-between"> 
                    <div className="w-9/12">
                    <h3 className="p-4 font-bold">{item.card.info.name} - {item.card.info.price / 100}</h3> 
                    <p>{item.card.info.description}</p>  
                    </div>
                    <div className="w-3/12">
                        <button className="bg-black text-white p-1">Add+</button>
                        <img className="w-44" src={CDN_URL + item.card.info.imageId}></img>
                    </div>
                    </div>
                    </div>
                    
            ))}
        </div>
    )
}

export default ItemCard;
